CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

CREATE SCHEMA IF NOT EXISTS cowsay;

-- TABLES 
CREATE TABLE IF NOT EXISTS cowsay.users (
    id TEXT PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),
    discord_id TEXT UNIQUE NOT NULL,
    created_at TIMESTAMP NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMP NOT NULL DEFAULT NOW(),
    deleted_at TIMESTAMP DEFAULT NULL
);

CREATE TABLE IF NOT EXISTS cowsay.servers (
    id TEXT PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),
    discord_id TEXT UNIQUE NOT NULL,
    created_at TIMESTAMP NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMP NOT NULL DEFAULT NOW(),
    deleted_at TIMESTAMP DEFAULT NULL
);

CREATE TABLE IF NOT EXISTS cowsay.cowfiles (
    id TEXT PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),
    name TEXT NOT NULL,
    data TEXT NOT NULL,
    author TEXT DEFAULT NULL,
    uploader_id TEXT NOT NULL REFERENCES cowsay.users(id),
    server_id TEXT REFERENCES cowsay.servers(id) DEFAULT NULL,
    created_at TIMESTAMP NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMP NOT NULL DEFAULT NOW(),
    deleted_at TIMESTAMP DEFAULT NULL
);

